function filterMenu(n,t){var u,f,r;for(u=n.value.toUpperCase(),f=document.getElementById(t),r=f.getElementsByTagName("li"),i=0;i<r.length;i++)stopName=r[i].getAttribute("name"),r[i].style.display=stopName.toUpperCase().indexOf(u)>-1?"":"none"}function ChangeFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t?$(n).html('<img class="favouritesStarImage" src="images/starRed.svg" />'):$(n).html('<img class="favouritesStarImage" src="images/starGray.svg" />')})}function RemoveFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t||$(n).parent().remove()})}function navigateWithDate(n){window.location.href=n+"&date="+$("#datepicker").val()}function initMap(){map=new google.maps.Map(document.getElementById("map"),{center:{lat:lat,lng:lng},zoom:17});var n=new google.maps.Marker({position:{lat:lat,lng:lng},map:map});stopsArray.forEach(function(n){new google.maps.Marker({position:{lat:n.stopLat,lng:n.stopLon},map:map})})}function tripPlannerSearchParams(n){$(n).attr("href",`/Stops/TripPlanner?stopFrom=${$("#stopFrom").val()}&stopTo=${$("#stopTo").val()}&date=${$("#datepicker").val()}`)}function chosenItemToInput(n,t){document.getElementById(t).value=n}$().ready(function(){$(function(){$("#accordion").accordion({collapsible:!0,active:!0,heightStyle:"content",autoHeight:!1,clearStyle:!0});$("#datepicker").datepicker({dateFormat:"yy-mm-dd",minDate:0});$("#datepicker").data("date")!==undefined&&$("#datepicker").datepicker("setDate",new Date($("#datepicker").data("date")))})});var map,lat,lng,stopsArray;