function filterMenu(){var i,r,u,t,f,n;for(i=document.getElementById("filterInput"),r=i.value.toUpperCase(),u=document.getElementById("filterList"),t=u.getElementsByTagName("li"),n=0;n<t.length;n++)f=t[n].getElementsByTagName("a")[0],t[n].style.display=f.innerHTML.toUpperCase().indexOf(r)>-1?"":"none"}function ChangeFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t?$(n).html('<img class="favouritesStarImage" src="images/starRed.svg" />'):$(n).html('<img class="favouritesStarImage" src="images/starGray.svg" />')})}function RemoveFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t||$(n).parent().remove()})}function navigateWithDate(n){window.location.href=n+"&date="+$("#timetableDate").val()}