function filterMenu(){var i,r,u,t,f,n;for(i=document.getElementById("filterInput"),r=i.value.toUpperCase(),u=document.getElementById("filterList"),t=u.getElementsByTagName("li"),n=0;n<t.length;n++)f=t[n].getElementsByTagName("a")[0],t[n].style.display=f.innerHTML.toUpperCase().indexOf(r)>-1?"":"none"}function ChangeFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t?$(n).html('<img class="favouritesStarImage" src="images/starRed.svg" />'):$(n).html('<img class="favouritesStarImage" src="images/starGray.svg" />')})}function RemoveFavourite(n,t,i,r){$.post("/Stops/AddToFavourite",{stopName:t,routeShortName:i,directionId:r},function(t){t||$(n).parent().remove()})}function navigateWithDate(n){window.location.href=n+"&date="+$("#timetableDate").val()}function initMap(){console.log(stopsArray);map=new google.maps.Map(document.getElementById("map"),{center:{lat:lat,lng:lng},zoom:17});var n=new google.maps.Marker({position:{lat:lat,lng:lng},map:map});stopsArray.forEach(function(n){new google.maps.Marker({position:{lat:n.stopLat,lng:n.stopLon},map:map})})}function tripPlannerSearchParams(n){$(n).attr("href",`/Stops/TripPlanner?stopFrom=${$("#stopFrom").val()}&stopTo=${$("#stopTo").val()}&date=${$("#datepicker").val()}`)}$(document).ready(function(){$(function(){$("#accordion").accordion({collapsible:!0,active:!0,heightStyle:"content",autoHeight:!1,clearStyle:!0});$("#datepicker").datepicker()})});var map,lat,lng,stopsArray;